/**
 * Берет все выбранные токены и добавляет их в боевой трекер. Затем бросает инициативу для всех токенов NPC и игроков.
 */
async function main() {
 await canvas.tokens.toggleCombat();
  game.combat.rollNPC({ messageOptions: { rollMode: CONST.DICE_ROLL_MODES.PRIVATE }})
game.combat.rollAll()
}
main();
