command:
// Макрос для Foundry, который позволяет пользователю открыть справочник Phenomen'a. 
new Dialog({
 title: "Справочник",
 content: `<iframe id="jsdos" src="https://5e.ruleplaying.com/" width="100%" height="100%"></iframe>`,
 buttons: {},
 render: html => {
        html.style.flex = "100%";
        html.querySelector("#jsdos").focus();
     }
}, { height: 600, width: 800, jQuery: false }).render(true);
