// Макрос для Foundry, который позволяет показать игрокам изображение выделенного токена. 
canvas.tokens.updateAll(
  token => ({ displayName : CONST.TOKEN_DISPLAY_MODES.HOVER }),
  token => token._controlled,
);
const popout = new 
ImagePopout(token.document.texture.src).render(true);
popout.shareImage();
