// Макрос для Foundry, который позволяет показать игрокам портрет выделенного токена. 
canvas.tokens.updateAll(
  token => ({ displayName : CONST.TOKEN_DISPLAY_MODES.HOVER }),
  token => token._controlled,
);

const popout = new ImagePopout(canvas.tokens.controlled[0].actor.img).render(true);
popout.shareImage();
