// Макрос для Foundry, который позволяет удалить все статусы с выбранного токена. 
(async () => {
    for ( let token of canvas.tokens.controlled ) {
        let effectsToDelete = token.actor.effects.filter(e => e.isTemporary)
        .map(e => { return e.id });
        await token.actor.deleteEmbeddedDocuments("ActiveEffect", effectsToDelete);
    }})();
